KanjaxPopupCss = ".kanji_popup {\n  width:         {{Math.min(WIDTH-36, WIDTH/2+200)}}px;\n  background-color: #ddf;\n  border:        1px solid #00f;\n  padding:       0.6em;\n  border-radius: 0.4em;\n  text-align:    center;\n}\n.night_mode #kanjax_popup {\n  background-color: #304040;\n  border:           1px solid #8080ff;\n}\n.night_mode #kanjax_popup img {\n  filter: invert(1);\n  -webkit-filter:invert(1);\n}\n.night_mode #kanjax_popup a {\n  color: #ffff00;\n}\n.kanji_popup div, .kanji_popup span, .kanji_popup td {\n  cursor: default;\n}\n/*.kanji_popup .editable:hover {\n  background-color: white;\n}*/\n.kanji_popup input{\n  font-weight: inherit;\n  font-family: inherit;\n  font-style:  inherit;\n  font-size:   80%;\n  word-break:  break-word;\n}\n.kanji_popup #strokes {\n  max-width: {{Math.min(WIDTH-36, WIDTH/2+200)-16}}px;\n  max-height: {{60+HEIGHT/50}}px;\n}\n.kanji_popup #onyomi, .kanji_popup #kunyomi {\n  font-style: normal;\n}\n.kanji_popup #kanji {\n  margin-right: 0.6em;\n}\n.kanji_popup #keyword {\n  margin-left: 0.6em;\n}\n.kanji_popup #yomi, .kanji_popup #strokes {\n  margin-top: 0.2em;\n  margin-bottom: 0.2em;\n}\n/*.kanji_popup .japanese { font-family: \"Kochi Mincho\"; }*/\n.kanji_popup .small {font-size: {{Math.round(10+WHMIN/80)}}px;}\n.kanji_popup .medium {font-size: {{Math.round(13+WHMIN/60)}}px;}\n.kanji_popup .large {font-size: {{Math.round(16+WHMIN/43)}}px;}\n.kanji_popup .huge  {font-size: {{Math.round(22+WHMIN/27)}}px;}\n.kanji_popup .bold  {font-weight: bold;}\n.kanji_popup .italic {font-style: italic;}\n\n.dict_popup {\n  max-height:    400px;\n  overflow:      auto;\n  width:         640px;\n  background-color: #e0e0ff;\n  border:        1px solid #ffffff;\n  padding:       12px;\n  border-radius: 17px;\n  text-align:    left;\n}\n.dict_popup .main {\n  font-size: 160%;\n}\n.dict_popup .othf {\n  color: #555;\n  font-size: 70%;\n}\n.dict_popup .pos {\n  color: #800;\n  font-size: 70%;\n  font-style: italic;\n  margin-top: 0.5em;\n  margin-left: -1.2em;\n}\n.dict_popup .misc {\n  color: #555;\n  font-size: 70%;\n}\n.dict_popup .refs {\n  color: #555;\n  font-size: 70%;\n}\n.dict_popup ol {\n  margin-top: 0.3em;\n  margin-bottom: 0.3em;\n}\n.dict_popup li {\n  color: #345;\n}\n.dict_popup li span {\n  color: black;\n}";
KanjaxCss = ".kanjax {\n    color:     #c00000 !important;\n    padding:   0px     !important;\n    border:    none    !important;\n    background-image: none !important;\n    display:   inline  !important;\n    cursor:    pointer !important;\n}\na .kanjax {\n    color: #a000a0  !important;\n    cursor: pointer !important;\n}\n.night_mode .kanjax {\n    color: #40ffff !important;\n}\n.night_mode a .kanjax {\n    color: #60ff60 !important;\n}\n.kanjax_lemma {\n    cursor:           pointer !important;\n    border-radius:    5px     !important;\n    padding:          1px     !important;\n}\n.kanjax_lemma:hover {\n    background-color: #ffffa0 !important;\n}\n.kanjax_ruby {\n    text-align: center    !important;\n    display: inline-block !important;\n}\n.kanjax_rt {\n    display: block !important;\n    font-size: 65%;\n    text-indent: 0px;\n    line-height: 0.9em;\n}\nrt, rp { font-size: 65%; }\nrt {\n    -moz-user-select: none;\n    -khtml-user-select: none;\n    -ms-user-select: none;\n    -webkit-user-select: none;\n    user-select: none;\n}\n";
KanjaxRubyCss = ".kanjax_ruby {\n    text-align: center;\n\n    /* fixes webkit flickering */\n    display: inline-block !important;\n}\n.emptied_by_me {\n    display: none !important;\n}\n.kanjax_rt {\n    display: block; /*!important;*/\n    text-indent: 0px;\n    line-height: 0.9em;\n\n    font-size: 65%;\n    -moz-user-select: none;\n    -khtml-user-select: none;\n    -ms-user-select: none;\n    -webkit-user-select: none;\n    -webkit-touch-callout: none;\n    user-select: none;\n    -webkit-text-size-adjust:0;\n}\n/*ruby {\n    display: inline-block;\n}\nrt {\n    display: block;\n\n    font-size: 65%;\n    -moz-user-select: none;\n    -khtml-user-select: none;\n    -ms-user-select: none;\n    -webkit-user-select: none;\n    -webkit-touch-callout: none;\n    user-select: none;\n    -webkit-text-size-adjust:0;\n}*/";
